import React, { useRef, useState } from 'react';
import { PageContainer, ProList, ProTable, ActionType, ProColumns } from '@ant-design/pro-components';
import { FileImageOutlined } from '@ant-design/icons';
import { Button, Image, Tag } from 'antd';
import { useRequest } from '@umijs/max';
import type { Classroom } from './data.d';
import { queryList } from './service';
import styles from './index.less';



const MonitoringManagement: React.FC = () => {
    const actionRef = useRef<ActionType>();
    const { data, loading } = useRequest(() => {
        return queryList()
    })

    const columns: ProColumns<Classroom>[] = [
        {
            title: "教室类型",
            dataIndex: 'type',
            valueEnum: {
                1: {
                    text: "一对一",
                    status: 'Processing',
                },
                2: {
                    text: "一对多",
                    status: 'Success',
                },
                3: {
                    text: "大班课",
                    status: 'Error',
                },
            },
        },
        {
            title: "教室状态",
            dataIndex: 'status',
            valueEnum: {
                1: {
                    text: "有人在的教室",
                    status: 'Processing',
                },
            },
        },
        {
            title: "教室号",
            dataIndex: 'code',
            valueType: 'textarea',
        },
        {
            title: "教室名称",
            dataIndex: 'name',
            valueType: 'textarea',
        },
        {
            title: "开始时间",
            dataIndex: 'startTime',
            valueType: 'dateTime',
        }
    ];

    const defaultData = data?.classroomData?.map((classroom: Classroom) => ({
        actions: [<a key="run">进入教室</a>, <a key="delete" style={styles.view}>查看</a>],
        title: null,
        content: (
            <div className={styles.listBox}>
                <Image
                    height={150}
                    className={styles.cover}
                    src="https://picsum.photos/230/150"
                    fallback="data:image/png;base64,"
                />
                <div className={styles.name}>{classroom.name}</div>
                <div className={styles.des}> <Tag color="#4F3CC9" bordered={false}>{classroom.type} </Tag><Tag color="#4F3CC9" bordered={false}>{classroom.size} </Tag><Tag color="#4F3CC9" bordered={false}>{classroom.duration} </Tag> </div>
                <div className={styles.des}>教室编号: {classroom.code}</div>
                <div className={styles.des}>实时在线人数: {classroom.onlineUsers}</div>
                <div className={styles.des}>开始时间: {classroom.startTime}</div>
                <div className={styles.des}>结束时间: {classroom.endTime}</div>
            </div>
        ),
    }));

    return (
        <PageContainer>
            {/* 表单 */}
            <ProTable<Classroom>
                actionRef={actionRef}
                rowKey="key"
                search={{
                    labelWidth: 80,
                    defaultCollapsed: false,
                }}
                tableClassName={styles.tableWrap}
                request={async () => ({ data: [] })}
                options={false}
                pagination={false}
                columns={columns}
            />
            {/* 列表 */}
            <ProList<any>
                ghost={false}
                itemCardProps={{
                    ghost: false,
                }}
                loading={loading}
                pagination={{
                    defaultPageSize: 10,
                    showSizeChanger: false,
                }}
                grid={{}}
                showActions="hover"
                metas={{
                    title: undefined,
                    content: {},
                    actions: {
                        cardActionProps: 'actions',
                    },
                }}
                headerTitle="教室查询"
                dataSource={defaultData}
            />
        </PageContainer>
    );
};

export default MonitoringManagement;

